      class AssetAssembler {
        static getPlayer(state = 'IDLE', color = "WHITE") {
          return ASSETS.SPRITES[state].FILE
            .replace(/##colorBase##/gi, ASSETS.COLORS[color].BASE.replace('#', ''))
            .replace(/##colorShadow##/gi, ASSETS.COLORS[color].SHADOW.replace('#', ''))
        }
      }

      class Player {
        constructor($dom, limits) {

          this.$player = $dom;
          this.playerBounds = this.$player.getBoundingClientRect();
          this.lastMovement = Date.now();
          this.axisX = (window.innerWidth / 2) - (this.playerBounds.width / 2);
          this.axisY = (window.innerHeight / 2) - (this.playerBounds.height / 2);
          this.limits = limits;
          this.keys = {top: false, right: false, down: false, left: false};

          window.addEventListener("keydown", (ev) => this.handleKeydown(ev), false);
          window.addEventListener("keyup", (ev) => this.handleKeyup(ev), false);

          this.walk(window.innerWidth / 3, 90);
          setTimeout(() => this.loop(), 24);
        }

        setLimits(limits) {
          this.limits = limits;
        }

        loop() {

          if (this.keys.left && (this.axisX - 10) > this.limits.xStart) {
            this.setWalking();
            this.setTurnLeft(true);
            this.walk(this.axisX - 10, this.axisY);
          }

          if (this.keys.right && (this.axisX + this.playerBounds.width + 10) < this.limits.xEnd) {
            this.setWalking();
            this.setTurnLeft(false);
            this.walk(this.axisX + 10, this.axisY);
          }

          if (this.keys.down && (this.axisY + this.playerBounds.height + 10) < this.limits.yEnd) {
            this.setWalking();
            this.walk(this.axisX, this.axisY + 10);
          }

          if (this.keys.up && (this.axisY - 10) > this.limits.yStart) {
            this.setWalking();
            this.walk(this.axisX, this.axisY - 10);
          }

          setTimeout(() => this.loop(), 24);
        }

        handleKeydown(ev) {
          switch (ev.code) {
            case "ArrowLeft":
            case "KeyA":
              this.keys.left = true;
              break;
            case "ArrowRight":
            case "KeyD":
              this.keys.right = true;
              break;
            case "ArrowDown":
            case "KeyS":
              this.keys.down = true;
              break;
            case "ArrowUp":
            case "KeyW":
              this.keys.up = true;
              break;
          }
        }

        walk(x, y) {
          this.axisX = x;
          this.axisY = y;
          this.$player.style.setProperty("--x", this.axisX + "px");
          this.$player.style.setProperty("--y", this.axisY + "px");
          this.lastMovement = Date.now();
        }

        handleKeyup(ev) {
          this.setWalking(false);

          switch (ev.code) {
            case "ArrowLeft":
            case "KeyA":
              this.keys.left = false;
              break;
            case "ArrowRight":
            case "KeyD":
              this.keys.right = false;
              break;
            case "ArrowDown":
            case "KeyS":
              this.keys.down = false;
              break;
            case "ArrowUp":
            case "KeyW":
              this.keys.up = false;
              break;
          }
        }

        checkPosition() {
          if (this.axisX < this.limits.xStart) this.walk(this.limits.xStart, this.axisY);
          if (this.axisY < this.limits.yStart) this.walk(this.axisX, this.limits.yStart);

          if ((this.axisX + this.playerBounds.width) > this.limits.xEnd) this.walk(this.limits.xEnd - this.playerBounds.width, this.axisY);
          if ((this.axisY + this.playerBounds.height) > this.limits.yEnd) this.walk(this.axisX, this.limits.yEnd - this.playerBounds.height);
        }

        setWalking(state = true) {
          this.$player.classList.toggle("c-player--walking", state);
        }

        setTurnLeft(state = true) {
          this.$player.classList.toggle("c-player--turn-left", state);
        }

        setColor(color = 'WHITE') {
          this.$player.style.setProperty("--sprite-idle", 'url("' + AssetAssembler.getPlayer('IDLE', color) + '") no-repeat center bottom');
          this.$player.style.setProperty("--sprite-walking", 'url("' + AssetAssembler.getPlayer('WALKING', color) + '") no-repeat center bottom');
        }

        show() {
          this.$player.classList.toggle("c-player--hide", false);
        }
      }

      let $player = document.querySelector(".c-player");


      let limits = document.body.getBoundingClientRect();

      limits.xStart = limits.x + 120;
      limits.yStart = limits.y + 20;
      limits.xEnd = limits.width - 20;
      limits.yEnd = 600;

      let player = new Player($player, limits);
      player.setColor('WHITE');

      const $this = this
      window.addEventListener('load', () => {
        $this.showCharacter = true
      });

      window.addEventListener('resize', () => {
        let limits = document.body.getBoundingClientRect();

        limits.xStart = limits.x + 120;
        limits.yStart = limits.y + 20;
        limits.xEnd = limits.width - 20;
        limits.yEnd = limits.height - 20;

        player.setLimits(limits);
        player.checkPosition();
      })

      document
        .querySelectorAll('.c-color')
        .forEach(el => el.addEventListener('click', (ev) => player.setColor(ev.target.querySelector('.c-color__input')
          .value), false))
    },
    changePlant() {
      if (this.plantNo > 2) {
        this.plantNo = 1
      } else {
        this.plantNo += 1
      }
    },
    changeBed() {
      if (this.bedStatus === 'on') {
        this.bedStatus = 'empty'
        this.showCharacter = true
      } else {
        this.bedStatus = 'on'
        this.showCharacter = false
      }
    },
    fridge() {
      const OutlineShader = {

        uniforms: {
          offset: {type: 'f', value: 0.01}
        },

        vertexShader: [

          "uniform float offset;",

          "void main() {",
          "  vec4 pos = modelViewMatrix * vec4( position + normal * offset, 1.0 );",
          "  gl_Position = projectionMatrix * pos;",
          "}"

        ].join('\n'),

        fragmentShader: [

          "void main() {",
          "  gl_FragColor = vec4( 0.0, 0.0, 0.0, 1.0 );",
          "}"

        ].join('\n')

      };

// -----------------------------------------------------

      const container = document.getElementById('fridge');

      const renderer = new THREE.WebGLRenderer({antialias: true, alpha: true});
      renderer.setClearColor(0x000000, 0);
      renderer.setPixelRatio(window.devicePixelRatio);
      renderer.setSize(window.innerWidth, window.innerHeight);
      container.appendChild(renderer.domElement);
      renderer.domElement.style.cursor = 'pointer';

      const camera = new THREE.PerspectiveCamera(40, window.innerWidth / window.innerHeight, .1, 100000);
      camera.position.set(-2.0, 2.6, 3.5);

      const controls = new THREE.OrbitControls(camera, renderer.domElement);
      controls.target.y = 0.9;
      controls.update();
      controls.enabled = false;

      const scene = new THREE.Scene();

      const aLight = new THREE.AmbientLight(0x404040);
      scene.add(aLight);

      const dLight = new THREE.DirectionalLight(0xffffff, 0.6);
      dLight.position.set(0.1, 0.7, 1);
      scene.add(dLight);

//

      const fbxLoader = new THREE.FBXLoader();

      fbxLoader.load('/fridge.fbx', function (fbx) {

        const fridgeGroup = fbx.children[0];

        const back = fridgeGroup.getObjectByName('back');
        const topDoor = fridgeGroup.getObjectByName('topDoor');
        const bottomDoor = fridgeGroup.getObjectByName('bottomDoor');
        const topHandle = fridgeGroup.getObjectByName('topHandle');
        const bottomHandle = fridgeGroup.getObjectByName('bottomHandle');

        // materials

        const fridgeMat = new THREE.MeshToonMaterial({
          color: 0xcccccc,
          shininess: 1,
          specular: 0xcccccc
        });

        const handleMat = new THREE.MeshToonMaterial({
          color: 0x777777,
          shininess: 1
        });

        back.material = topDoor.material = bottomDoor.material = fridgeMat;
        topHandle.material = bottomHandle.material = handleMat;

        // outline

        const outlineMat = new THREE.ShaderMaterial({
          uniforms: THREE.UniformsUtils.clone(OutlineShader.uniforms),
          vertexShader: OutlineShader.vertexShader,
          fragmentShader: OutlineShader.fragmentShader,
          side: THREE.BackSide
        });

        const children = [back, topDoor, bottomDoor, topHandle, bottomHandle];
        children.forEach(function (obj) {
          const outline = new THREE.Mesh(obj.geometry, outlineMat);
          obj.add(outline);
        });

        scene.add(fridgeGroup);

        topDoor.userData.isOpen = false;
        bottomDoor.userData.isOpen = false;

        // input

        function toggleDoor(door) {

          if (door.userData.isOpen) {

            const close = new TWEEN.Tween(door.rotation)
              .to({y: 0}, 200)
              .easing(TWEEN.Easing.Exponential.In)
              .start();

            // impact from the slam
            const slam = new TWEEN.Tween(fridgeGroup.rotation)
              .to({y: 0}, 200)
              .delay(200)
              .easing(TWEEN.Easing.Quintic.Out)
              .start();

          } else {

            const open = new TWEEN.Tween(door.rotation)
              .to({y: Math.PI / 2}, 800)
              .easing(TWEEN.Easing.Elastic.Out)
              .start();

            // so impact doesn't have to reset
            const slide = new TWEEN.Tween(fridgeGroup.rotation)
              .to({y: THREE.Math.degToRad(10)}, 200)
              .delay(150)
              .easing(TWEEN.Easing.Cubic.Out)
              .start();
          }

          door.userData.isOpen = !door.userData.isOpen;

        }

        let which = true;
        let count = 0;

        function mousedown(e) {

          TWEEN.removeAll();

          let door = which ? topDoor : bottomDoor;
          door.rotation.y = door.userData.isOpen ? Math.PI / 2 : 0;

          count++;
          if (count % 2) which = !which;

          door = which ? topDoor : bottomDoor;
          toggleDoor(door);

        }

        renderer.domElement.addEventListener('mousedown', mousedown, false);

      });

//

      window.addEventListener('resize', resize, false);

      function resize() {
        camera.aspect = window.innerWidth / window.innerHeight;
        camera.updateProjectionMatrix();
        renderer.setSize(window.innerWidth, window.innerHeight);
      }

      loop();

      function loop() {
        requestAnimationFrame(loop);
        TWEEN.update();
        renderer.render(scene, camera);
      }
    }
  },
}
</script>

