  --sprite-hat: url('~@/assets/images/train/helmet.png') no-repeat 9px 22px;
  position: relative;
  width: 85px;
  height: 150px;
  transform: translate(var(--x), var(--y));
  transition: opacity 0.3s ease-in-out;
}

.c-player:before, .c-player:after {
  content: "";
  height: 100%;
  width: 100%;
  position: absolute;
  display: block;
  filter: blur(0);
}

.c-player:before {
  background: var(--sprite-idle);
}

.c-player:after {
  background: var(--sprite-hat);
  filter: blur(0);
}

.c-player--walking:before {
  background: var(--sprite-walking);
  animation: PlayerWalking 0.8s steps(12) infinite;
}

.c-player--walking:after {
  animation: HatWalking 0.8s step-start infinite;
}

.c-player--turn-left:before, .c-player--turn-left:after {
  transform: scalex(-1);
}

.c-player--hide {
  opacity: 0;
}

@keyframes PlayerWalking {
  from {
    background-position-x: 0px;
  }
  to {
    background-position-x: -1020px;
  }
}

@keyframes HatWalking {
  0%, 100% {
    background-position: 12px 30px;
  }
  25%, 75% {
    background-position: 12px 25px;
  }
  50% {
    background-position: 12px 30px;
  }
}

.c-color {
  --base: var(--color-white-base);
  --shadow: var(--color-white-shadow);
  height: 2rem;
  width: 2rem;
  border: 3px solid #000;
  border-radius: 8px;
  display: block;
  overflow: hidden;
  position: relative;
}

.c-color:after, .c-color:before {
  content: "";
  display: block;
  position: absolute;
  top: -4px;
  left: -4px;
  height: calc(100% + 8px);
  width: calc(100% + 8px);
  border-radius: 16px;
  cursor: pointer;
}

.c-color:after {
  background-color: var(--base);
